import React, { useState, useEffect } from 'react';
import { auth, db } from '../utilities/firebase'; // Make sure Firebase is set up
import { collection, addDoc, query, orderBy, onSnapshot } from 'firebase/firestore';
import '../styles/Messaging.css';

const Messaging = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const q = query(collection(db, 'messages'), orderBy('createdAt', 'asc'));
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      let messagesList = [];
      querySnapshot.forEach((doc) => {
        messagesList.push({ ...doc.data(), id: doc.id });
      });
      setMessages(messagesList);
    });

    return () => unsubscribe();
  }, []);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    const { uid, displayName } = auth.currentUser;
    try {
      await addDoc(collection(db, 'messages'), {
        text: message,
        uid,
        displayName,
        createdAt: new Date(),
      });
      setMessage(''); // Clear input after sending
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="messaging-container">
      <header className="messaging-header">
        <h2>Secure Messaging</h2>
        <p>Communicate with other developers in real-time. Messages sent here are visible to all authenticated users.</p>
      </header>

      <div className="messages">
        {messages.map((msg) => (
          <div key={msg.id} className={`message ${msg.uid === auth.currentUser.uid ? 'sent' : 'received'}`}>
            <strong>{msg.displayName}</strong>: {msg.text}
          </div>
        ))}
      </div>

      <form onSubmit={sendMessage} className="message-form">
        <input
          type="text"
          placeholder="Type your message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default Messaging;
